import React  from 'react';
import logo from './logo.svg';
import './App.css';
import Button from '@material-ui/core/Button'
import ButtonGroup from '@material-ui/core/ButtonGroup'
import SaveIcon from '@material-ui/icons/Save'
import DeleteIcon from '@material-ui/icons/Delete'
import Checkbox from '@material-ui/core/Checkbox'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import TextField from '@material-ui/core/TextField'

import { makeStyles, ThemeProvider, createMuiTheme } from '@material-ui/core/styles'
import { blue, pink } from '@material-ui/core/colors'
import 'fontsource-roboto';
import Typography from '@material-ui/core/Typography'
import Container from '@material-ui/core/Container'
import Paper from '@material-ui/core/Paper'
import Grid from '@material-ui/core/Grid'


import AppBar from '@material-ui/core/AppBar'
import Toolbar from '@material-ui/core/Toolbar'
import IconButton from '@material-ui/core/IconButton'
import MenuIcon from '@material-ui/icons/Menu'



const useStyles = makeStyles({
  root: {
    background: 'linear-gradient(45deg, blue, red)',
    border: 0,
    marginBottom: 10,
    borderRadius: 1000,
    color: 'white',
    padding: '30px 30px'
  }
})

const theme = createMuiTheme({
  typography:{
    h2: {
      fontSize: 30,
      marginBottom: 5,
      // background: 'radial-gradient(45deg, pink, red)',
    }
  },
  // palette: {
  //   primary: {
  //     main: pink[500],
  //   },
  //   secondary: {
  //     main: blue[500],
  //   }
  // }
})


function ButtonStyled() {
  const classes = useStyles();
  return <Button className= {classes.root} >This is my website</Button>
}

function CheckboxExample()
{
  const [checked, setChecked] = React.useState(true)
  return (
    <FormControlLabel 
    control={<Checkbox 
      checked={checked}
      icon={<SaveIcon />}
      checkedIcon = {<SaveIcon />}
      onChange = {(e) => setChecked(e.target.checked)} 
      inputProps={{
        'aria-label' : 'secondary checkbox'
      }}
    />}
    label= "Testing Checkbox"
    />

  )
}

function App() {
  return (
    <ThemeProvider theme= {theme} >
     <Container  >
        <div className="App">
          <header className="App-header">
            <AppBar color = "secondary" >
              <Toolbar>
                <IconButton>
                  <MenuIcon/>
                </IconButton>
                <Typography variant="h6">
                  MUI Themeing
                </Typography>
                <Button>
                  Login
                </Button>
              </Toolbar>
            </AppBar>
            <Typography variant='h2'>
            Welcome Home!
            </Typography>

            <Typography variant='subtitle1'>
              This is cool
            </Typography>

          <ButtonStyled/>

          <Grid container spacing={2} justify="center">
          <Grid item xs={3} sm={5} md={7} lg={9} >
              <Paper style = {{ height:75, width:'100%', }} />
            </Grid>
            <Grid item xs={3} sm={5} md={7} lg={9} >
              <Paper style = {{ height:75, width:'100%', }} />
            </Grid>
            <Grid item xs={3} sm={5} md={7} lg={9} >
              <Paper style = {{ height:75, width:'100%', }} />
            </Grid>
          </Grid>

          {/* <ButtonGroup color = "secondary">
            <TextField
            variant = "filled"
            type="email"
            label = "Type email"
            placeholder = "test.test@gmail.com"
            />
            {/* <TextField
            variant = "filled"
            type="time"
            label = "The Time"
            /> 
            {/* <TextField
            variant = "outlined"
            style={{
                fontSize: 18
              }}
            type="date"
            />
            </ButtonGroup> */}
            <img src={logo} className="App-logo" alt="logo" /> 
            <CheckboxExample/>
            <ButtonGroup variant="contained"
            color="primary">
            <Button
            startIcon = {<SaveIcon />}
            onClick={()=> alert('Hello')} 
            size="large"
            // disabled
            // href="#"
            // style={{
            //   backgroundColor: "linear-gradient(45deg, blue, pink)"
            // }}
            // variant="contained" 
            // color="primary" 
            >
              Save
            </Button>
            <Button
            endIcon = {<DeleteIcon />}
            onClick={()=> alert('Bye')} 
            size="large"
            href="#">
              Delete
            </Button>
            </ButtonGroup>

          </header>

        </div>
        </Container>
    </ThemeProvider>
  );
}

export default App;
